<?phpdefined('BASEPATH') or exit('No direct script access allowed');class About_us_c extends CI_Controller{    function __construct()    {        parent::__construct();        check_not_login();        $this->load->model(['About_us_m']);        if ($this->session->userdata('site_lang')) {            switch ($_SESSION['site_lang']) {                case 'arabic':                    $this->config->set_item('language', 'arabic');                    break;                case 'english':                    $this->config->set_item('language', 'english');                    break;                case 'russian':                    $this->config->set_item('language', 'russian');                    break;                default:                    $this->config->set_item('language', 'english');                    break;            }        }    }    public function index()    {        $data['all_company_stats'] = $this->About_us_m->get()->result();        $data['title'] = translate('About_company');        $this->template->load('template', 'about_us/about_us_data', $data);    }    function get_ajax_company_stats()    {        $list = $this->About_us_m->get_datatables_company_stats();        $data = array();        $no = @$_POST['start'];        foreach ($list as $item) {            $no++;            $row = array();            $row[] = $no . ".";            $row[] = $item->about_us_short;            $row[] = $item->about_us_short_en;/*            $row[] = $item->about_us_short_ru;*///            $row[] = $item->description;            $row[] = '            <div class="btn-block  btn-group-sm">            <a href="' . base_url() . 'About_us_c/edit_about_us/' . $item->id . '" title="'.translate('Update').'"   class="btn btn-info btn-sm">                                            <i class="fa fa-pencil-alt" > </i></a>                                                                    </div>            ';            $data[] = $row;        }        $output = array(            "draw" => @$_POST['draw'],            "recordsTotal" => $this->About_us_m->count_all_company_stats(),            "recordsFiltered" => $this->About_us_m->count_filtered_company_stats(),            "data" => $data,        );        // output to json format        echo json_encode($output);    }    public function get_load_details()    {        $id = $this->input->post('id');        //$data['all_details'] = $this->About_us_m->get_load_details($id)[0];        $data['all_details'] = $this->About_us_m->get($id)->row();        $this->load->view('about_us/load_details_page', $data);    }    /****************************************************************/    public function thumb($data, $folder_name)    {        $config['image_library'] = 'gd2';        $config['source_image'] = $data['full_path'];        $config['new_image'] = 'uploads/' . $folder_name . '/thumbs/' . $data['file_name'];        $config['create_thumb'] = TRUE;        $config['maintain_ratio'] = TRUE;        $config['thumb_marker'] = '';        $config['width'] = 275;        $config['height'] = 250;        $this->load->library('image_lib', $config);        $this->image_lib->initialize($config);        $this->image_lib->resize();    }    private function upload_image($file_name, $folder_name)    {        $config['upload_path'] = 'uploads/' . $folder_name;        $config['allowed_types'] = 'gif|Gif|ico|ICO|jpg|JPG|jpeg|JPEG|BNG|png|PNG|bmp|BMP|WMV|wmv|MP3|mp3|FLV|flv|SWF|swf';        $config['max_size'] = '1024*8';        $config['encrypt_name'] = true;        $this->load->library('upload', $config);        $this->upload->initialize($config);        if (!$this->upload->do_upload($file_name)) {            return false;        } else {            $datafile = $this->upload->data();            $this->thumb($datafile, $folder_name);//            echo '<br>'. $datafile['file_name'];            return $datafile['file_name'];        }    }    public function add_about_us() /* About_us/add_company_stats*/    {        $this->load->helper(array('form', 'url'));        $this->load->library(array('form_validation'));        $this->form_validation->set_rules('about_us', ''.translate('About_the_application_in_Arabic').'', 'required');        $this->form_validation->set_rules('about_us_en', ''.translate('About_the_application_in_English').'', 'required');        $this->form_validation->set_rules('about_us_short', ''.translate('About_the_application_in_Arabic_(brief)').'', 'required');        $this->form_validation->set_rules('about_us_short_en', ''.translate('About_the_application_in_English_(brief)').'', 'required');        $this->form_validation->set_rules('our_vision', ''.translate('Our_vision_in_Arabic').'', 'required');        $this->form_validation->set_rules('our_vision_en', ''.translate('Our_vision_in_English').'', 'required');        $this->form_validation->set_rules('our_mission', ''.translate('Our_mission_in_Arabic').'', 'required');        $this->form_validation->set_rules('our_mission_en', ''.translate('Our_mission_in_English').'', 'required');        $this->form_validation->set_rules('our_goals', ''.translate('Our_goals_in_Arabic').'', 'required');        $this->form_validation->set_rules('our_goals_en', ''.translate('Our_goals_in_English').'', 'required');        if ($this->form_validation->run() == FALSE) {            $company_stats = new stdClass();            $company_stats->id = null;            $company_stats->company_clients = null;            $company_stats->company_services = null;            $company_stats->company_projects = null;            $company_stats->company_projects_done = null;            $data = array(                'page' => 'add',                'company_stats' => $company_stats,            );            $this->template->load('template', 'about_us/about_us_form', $data);        } else {            $post = $this->input->post(null, TRUE);            if (isset($_POST['add'])) {                $this->About_us_m->add_company_stats($post);                if ($this->db->affected_rows() > 0) {                    $this->session->set_flashdata('sukses',translate('Process_Done_Successfully'));                }                redirect('About_us_c', 'refresh');            }        }    }    public function edit_about_us($id)    {        $this->load->helper(array('form', 'url'));        $this->load->library('form_validation');        $this->form_validation->set_rules('about_us', ''.translate('About_the_application_in_Arabic').'', 'required');        $this->form_validation->set_rules('about_us_en', ''.translate('About_the_application_in_English').'', 'required');        $this->form_validation->set_rules('about_us_short', ''.translate('About_the_application_in_Arabic_(brief)').'', 'required');        $this->form_validation->set_rules('about_us_short_en', ''.translate('About_the_application_in_English_(brief)').'', 'required');        $this->form_validation->set_rules('our_vision', ''.translate('Our_vision_in_Arabic').'', 'required');        $this->form_validation->set_rules('our_vision_en', ''.translate('Our_vision_in_English').'', 'required');        $this->form_validation->set_rules('our_mission', ''.translate('Our_mission_in_Arabic').'', 'required');        $this->form_validation->set_rules('our_mission_en', ''.translate('Our_mission_in_English').'', 'required');        $this->form_validation->set_rules('our_goals', ''.translate('Our_goals_in_Arabic').'', 'required');        $this->form_validation->set_rules('our_goals_en', ''.translate('Our_goals_in_English').'', 'required');        if ($this->form_validation->run() == FALSE) {            $query = $this->About_us_m->get($id);            if ($query->num_rows() > 0) {                $company_stats = $query->row();                $data = array(                    'page' => 'edit',                    'company_stats' => $company_stats,                );                $this->template->load('template', 'about_us/about_us_edit', $data);            } else {                redirect('About_us_c', 'refresh');            }        } else {            $post = $this->input->post(null, TRUE);            if (isset($_POST['add'])) {                $this->About_us_m->edit_company_stats($post);            }            if ($this->db->affected_rows() > 0) {                $this->session->set_flashdata('sukses',translate('Process_Done_Successfully'));            } else {                $this->session->set_flashdata('danger', translate('error_try_again'));            }            redirect('About_us_c', 'refresh');        }    }    public function delete_about_us($id)    {        $this->About_us_m->delete_company_stats($id);        if ($this->db->affected_rows() > 0) {            $this->session->set_flashdata('success', translate('Delete_Done_Successfully'));        }        redirect('About_us_c', 'refresh');    }}