<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Client extends CI_Controller {    function __construct()    {        parent::__construct();        check_not_login();        $this->load->model(['Client_m']);    }    public function index()    {        //$data['all_client'] = $this->Client_m->get();        $data['title']='عملاء الشركة';        $this->template->load('template','client/client_data',$data);    }    function  get_ajax_client()    {        $list = $this->Client_m->get_datatables_client();        $data = array();        $no = @$_POST['start'];        foreach ($list as $item) {            $no++;            $row = array();            if (!empty($item->image) && (file_exists('uploads/client/' . $item->image))) {                $image= base_url() . 'uploads/client/' . $item->image;            } else {                $image=  base_url() . 'uploads/defult_image.jpg';            }            $row[] = $no . ".";            $row[] = '<img src="' . $image. '"  height="100px" width="150px" alt="" class="direct-chat-img" />';            $row[] = $item->name;            $row[] = $item->name_en;            $row[] = '<a href="'.$item->link.'" target="_blank" title="انقر للذهاب الي اللينك" class="btn btn-info btn-sm"><i class="fas fa-link"></i></a>';//            $row[] = $item->description;            $row[]='            <div class="btn-block  btn-group-sm">                                    <a href="#modal_details" data-toggle="modal" title="تفاصيل"                         onclick="get_load_details('.$item->id.')" class="btn btn-warning btn-sm">                          <i class="fa fa-eye" style="color: white;"></i></a>                                         <a href="'.base_url().'Client/edit_client/'.$item->id.'" title="تعديل"   class="btn btn-info btn-sm">                                            <i class="fa fa-pencil-alt" > </i></a>            <a href="#" title="حذف"  onclick=\'swal({                                            title: "هل انت متأكد من الحذف ؟",                                            text: "",                                            type: "warning",                                            showCancelButton: true,                                            confirmButtonClass: "btn-danger",                                            confirmButtonText: "حذف",                                            cancelButtonText: "إلغاء",                                            closeOnConfirm: false                                            },                                            function(){                                            swal("تم الحذف!", "", "success");                                            window.location="'.base_url().'Client/delete_client/'.$item->id.'";                                            });\' class="btn btn-danger btn-sm">                                            <i class="fa fa-trash"> </i></a>                                                                    </div>            ';            $data[] = $row;        }        $output = array(            "draw" => @$_POST['draw'],            "recordsTotal" => $this->Client_m->count_all_client(),            "recordsFiltered" => $this->Client_m->count_filtered_client(),            "data" => $data,        );        // output to json format        echo json_encode($output);    }    public function get_load_details()    {        $id = $this->input->post('id');        //$data['all_details'] = $this->Client_m->get_load_details($id)[0];        $data['all_details'] = $this->Client_m->get($id)->row();        $this->load->view('client/load_details_page', $data);    }    /****************************************************************/    public function thumb($data,$folder_name)    {        $config['image_library'] = 'gd2';        $config['source_image'] = $data['full_path'];        $config['new_image'] = 'uploads/'.$folder_name.'/thumbs/' . $data['file_name'];        $config['create_thumb'] = TRUE;        $config['maintain_ratio'] = TRUE;        $config['thumb_marker'] = '';        $config['width'] = 275;        $config['height'] = 250;        $this->load->library('image_lib', $config);        $this->image_lib->initialize($config);        $this->image_lib->resize();    }    private  function upload_image($file_name,$folder_name){        $config['upload_path'] = 'uploads/'.$folder_name;        $config['allowed_types'] = 'gif|Gif|ico|ICO|jpg|JPG|jpeg|JPEG|BNG|png|PNG|bmp|BMP|WMV|wmv|MP3|mp3|FLV|flv|SWF|swf';        $config['max_size']    = '1024*8';        $config['encrypt_name']=true;        $this->load->library('upload',$config);        $this->upload->initialize($config);        if(! $this->upload->do_upload($file_name)){            return  false;        }else{            $datafile = $this->upload->data();            $this->thumb($datafile,$folder_name);//            echo '<br>'. $datafile['file_name'];            return  $datafile['file_name'];        }    }    public function add_client() /* Client/add_client*/    {        $this->load->helper(array('form','url'));        $this->load->library(array('form_validation'));        $this->form_validation->set_rules('name', ' الاسم بالعربي', 'required');        $this->form_validation->set_rules('name_en', 'الاسم بالانجليزي', 'required');        $this->form_validation->set_rules('link', 'اللينك', 'required');        $this->form_validation->set_rules('description', ' الوصف بالعربي', '');        $this->form_validation->set_rules('description_en', ' الوصف بالانجليزي', '');        if (empty($_FILES['image']['name']))        {            $this->form_validation->set_rules('image', 'اللوجو ', 'required');        }        if ($this->form_validation->run() == FALSE) {            $client = new stdClass();            $client->id = null;            $client->description = null;            $client->image = null;            $data = array(                'page' => 'add',                'client' => $client,            );            $this->template->load('template', 'client/client_form', $data);        } else {            $post = $this->input->post(null, TRUE);            if (isset($_POST['add'])) {                $image = $this->upload_image("image",'client');                $this->Client_m->add_client($post,$image);                if ($this->db->affected_rows() > 0) {                    $this->session->set_flashdata('success', 'تمت العملية بنجاح ');                }                redirect('Client', 'refresh');            }        }    }    public function edit_client($id)    {        $this->load->helper(array('form', 'url'));        $this->load->library('form_validation');        $this->form_validation->set_rules('name', 'الاسم', 'required');        $this->form_validation->set_rules('link', 'اللينك', 'required');        $this->form_validation->set_rules('description', ' الوصف', '');                if ($this->form_validation->run() == FALSE) {            $query = $this->Client_m->get($id);            if ($query->num_rows() > 0) {                $client = $query->row();                $data = array(                    'page' => 'edit',                    'client' => $client,                );                $this->template->load('template', 'client/client_edit', $data);            } else {                redirect('Client', 'refresh');            }        } else {            $post = $this->input->post(null, TRUE);            if (isset($_POST['add'])) {                $image = $this->upload_image("image",'client');                $this->Client_m->edit_client($post,$image);            }            if ($this->db->affected_rows() > 0) {                $this->session->set_flashdata('success', 'تمت العملية بنجاح ');            }else{                $this->session->set_flashdata('danger', 'حدث خطأ يرجي المحاوله مره أخري ');            }            redirect('Client', 'refresh');        }    }    public function delete_client($id){        $this->Client_m->delete_client($id);        if($this->db->affected_rows() >0){            $this->session->set_flashdata('success', 'تم الحذف بنجاح');        }        redirect('Client','refresh');    }}