<?phpclass Team_m extends CI_Model{    var $column_order_groups = array(null,'team.image','team.name','team.name_en','team.name_ru','team.job_title','team.job_title_en','team.job_title_ru'); //set column field database for datatable orderable    var $column_search_groups = array('team.image','team.name','team.name_en','team.name_ru','team.job_title','team.job_title_en','team.job_title_ru'); //set column field database for datatable searchable    var $order_groups = array('team.id' => 'desc');    private function _get_datatables_query_team()    {        $this->db->select('team.*');        $this->db->from('tbl_team as team');        $i = 0;        foreach ($this->column_search_groups as $item) {            if (@$_POST['search']['value']) {                if ($i === 0) { // first loop                    $this->db->group_start();                    $this->db->like($item, $_POST['search']['value']);                } else {                    $this->db->or_like($item, $_POST['search']['value']);                }                if (count($this->column_search_groups) - 1 == $i)                    $this->db->group_end();            }            $i++;        }        if (isset($_POST['order'])) {            $this->db->order_by($this->column_order_groups [$_POST['order']['0']['column']], $_POST['order']['0']['dir']);        } else if (isset($this->order_groups)) {            $order = $this->order_groups;            $this->db->order_by(key($order), $order[key($order)]);        }    }    function get_datatables_team()    {        $this->_get_datatables_query_team();        if (@$_POST['length'] != -1)            $this->db->limit(@$_POST['length'], @$_POST['start']);        $query = $this->db->get();        return $query->result();    }    function count_filtered_team()    {        $this->_get_datatables_query_team();        $query = $this->db->get();        return $query->num_rows();    }    function count_all_team()    {        $this->db->select('team.*');        $this->db->from('tbl_team as team');        return $this->db->count_all_results();    }    /*************************************************************************/    public function get($id = null)    {        $this->db->select('tbl_team.* , users.name as publisher_name ');        $this->db->from('tbl_team');        $this->db->join('users','users.user_id = tbl_team.user_id');        if ($id != null) {            $this->db->where('id', $id);        }        $this->db->order_by('id','desc');        $query = $this->db->get();        return $query;    }    public function add_team($post,$image='')    {        $params = [            'user_id' => $this->session->userdata('userid'),            'image' => $image,            'name' => $post['name'],            'name_en' => $post['name_en'],            'name_ru' => $post['name_ru'],            'job_title' => $post['job_title'],            'job_title_en' => $post['job_title_en'],            'job_title_ru' => $post['job_title_ru'],            'created_at' => date('Y-m-d H:i:s')        ];        $this->db->insert('tbl_team', $params);    }    public function edit_team($post,$image='')    {        $params = [            'name' => $post['name'],            'name_en' => $post['name_en'],            'name_ru' => $post['name_ru'],            'job_title' => $post['job_title'],            'job_title_en' => $post['job_title_en'],            'job_title_ru' => $post['job_title_ru'],            'updated' => date('Y-m-d H:i:s')        ];        if(!empty($image)) {            $params['image'] = $image;        }        $this->db->where('id',$post['id']);        $this->db->update('tbl_team',$params);    }    public function delete_team($id)    {        $item = $this->get($id)->row();        if($item->image != null){            $target_file = './uploads/team/'.$item->image ;            unlink($target_file);        }        $this->db->where('id',$id);        $this->db->delete('tbl_team');    }}?>