<style>    .form-group .help-block.form-error {        display: block !important;        position: unset !important;    }</style><section class="content">    <div class="container-fluid">        <!-- SELECT2 EXAMPLE -->        <div class="card card-primary card-outline">            <div class="card-header">                <h3 class="card-title"> <?=translate('Update_Video_Data')?></h3>                <div class="card-tools">                    <div class="float-left">                        <a href="<?= site_url('Video_c') ?>" class="btn btn-warning btn-flat">                            <i class="fa fa-undo"></i>                            <?=translate('Back')?></a>                    </div>                </div>            </div>            <!-- /.card-header -->            <div class="card-body">                <?php echo form_open_multipart('Video_c/edit_video/' . $company_stats->id, array("id" => 'form_div')); ?>                <input type="hidden" name="id" value="<?= $company_stats->id ?>"/>                <div class="row">                    <div class="col-md-6">                        <div class="form-group">                            <label><?=translate('The_Date')?></label>                            <input type="date" name="date" class="form-control"                                   id="isi" value="<?= $company_stats->date_ar?>"/>                        </div>                        <?php if (form_error('date')) {                            echo "<span style='color:red'>" . form_error('date') . "</span>";                        } ?>                    </div>                    <div class="col-md-6">                        <div class="form-group">                            <label><?=translate('The_Video_Link')?></label>                            <a href="#modal_details" data-toggle="modal" title="<?=translate('The_Video_Link')?>"                               onclick="get_load_details(<?= $company_stats->id?>)"  >                                 <i class="fa fa-eye"></i><?=translate('View_The_Link')?></a>                            <input type="file" name="video_link" class="form-control "                                   id="isi" accept="video/mp4,video/x-m4v,video/*" />                        </div>                        <?php if (form_error('video_link')) {                            echo "<span style='color:red'>" . form_error('video_link') . "</span>";                        } ?>                    </div>                </div>                <div class="form-group">                    <button type="submit" class="btn btn-labeled btn-success " name="add"                            value="add"><i class="fa fa-paper-plane"></i><?=translate('Save')?>                    </button>                </div>                <?php echo form_close(); ?>                <!-- /.row -->            </div>            <!-- /.card-body -->        </div>        <!-- /.card -->    </div></section><div class="modal fade bd-example-modal-lg" id="modal_details" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">    <div class="modal-dialog modal_details modal-lg" role="document" style="width: 90%">        <div class="modal-content">            <div class="modal-header" style="direction: rtl!important;">                <button type="button" class="close" data-dismiss="modal" aria-label="Close">                    <span aria-hidden="true">&times;</span>                </button>                <h4 class="modal-title"><?=translate('The_Video_Link')?></h4>            </div>            <div class="modal-body">                <div id="details"></div>            </div>            <div class="modal-footer" style=" display: inline-block;width: 100%;">                <button type="button" class="btn btn-danger"                        data-dismiss="modal"><?=translate('Close')?>                </button>            </div>        </div>    </div></div><script>    function get_load_details(valu) {        $.ajax({            type: 'post',            url: "<?php echo base_url();?>Video_c/get_load_details",            data: {                id: valu            },            success: function(d) {                $('#details').html(d);            }        });    }</script>